import React from 'react'
import {useState,useEffect} from 'react'
import { useParams,useNavigate} from 'react-router-dom'
import { useSelector } from 'react-redux';
import "./NgoProfile.css";
const NgoProfile = () => {
    const user=  useSelector((state)=>{return state.user});
    const navigate = useNavigate();
    const [ngo,setNgo] = useState(null);
    const [events,setEvents] = useState([]);

    const {id} = useParams();
    useEffect(() => {
     fetchNgo();
     fetchAllEvents();
    }, [])
    const fetchNgo = async()=>{
        const response = await fetch(`http://localhost:8001/ngo/${id}`);
        const json = await response.json();
        setNgo(json.data);
    }
    const fetchAllEvents = async()=>{
        const response = await fetch(`http://localhost:8001/event/`);
        const json = await response.json();
        console.log(json)
        setEvents(json.data);
    }
  return (
    <div className='ngo-body'>
       { ngo &&  <div className="ngo-box">
            <img src={`http://localhost:8001/assets/${ngo.profile}`} alt="" />
            <h3>Name:{ngo.name} </h3>
            <h3>Establishment Year:{ngo.estb} </h3>
            <h3>Email:{ngo.email} </h3>
             {ngo._id===user._id?<h2>Followers: {ngo.followers.length}</h2>:<h2>Followers: {ngo.followers.length}<button>Follow</button></h2>} 
            {ngo._id===user._id?<button onClick={()=>{navigate("/event/create")}}>Create Event</button>:""}
        </div>
    }
    {
        ngo && <div className="ngo-events">
                <h1> Events</h1>
                <ul>
                {
                    events.map((e)=>{
                        if(e.organiser===ngo._id) return <li> 

                        </li>
                    })
                }
                </ul>
        </div>
    }

    </div>
  )
}

export default NgoProfile
