import React,{useState} from 'react'
import {useNavigate} from 'react-router-dom';
import { Link } from 'react-router-dom';
import './RegisterNgo.css';
const RegisterNgo = () => {
    const renderYearOptions = () => {
        const startYear = 1950;
        const endYear = 2023;
        const yearOptions = [];

        for (let year = startYear; year <= endYear; year++) {
          yearOptions.push(<option key={year} value={year}>{year}</option>);
        }

        return yearOptions;
      };
    const [data,setData] = useState({
        name:"",
        email:"",
        password:"",
        cpassword:"",
        regNo:"",
        estb:"",
    });
    const [profile,setProfile] = useState("");
    const navigate = useNavigate();
    const handleSubmit = async(e)=>{
        try {
            const formData = new FormData();
            formData.append("name",data.name);
            formData.append("email",data.email);
            formData.append("password",data.password);
            formData.append("profile",profile);
            formData.append("regNo",data.regNo);
            formData.append("estb",data.estb);

            const response = await fetch("http://localhost:8001/ngo/register",{
                method:"POST",
                body:formData
            });
            const json = await response.json();
            console.log(json)
            if(json.success) 
            {
                alert("Created");
                navigate("/login/ngo");
            }
            else 
                alert("failed");
        }catch(err)
        {
            alert(err.message);
        }   
    }
  return (
    <div className='register-ngo-body'>
        <h1>REGISTER NGO</h1>
         <div className="login-container">
        <div className="form">

            <input
                type="text"
                placeholder='NGO Name' 
                value={data.name}
                onChange={(e)=>{setData({...data,name:e.target.value})}}
                />

            <input
                type="email"
                placeholder='Email' 
                value={data.email}
                onChange={(e)=>{setData({...data,email:e.target.value})}}
                />
            <input
                type="password"
                placeholder='Password' 
                value={data.password}
                onChange={(e)=>{setData({...data,password:e.target.value})}}
                />
            <input
                type="password"
                placeholder='Confirm Password' 
                value={data.cpassword}
                onChange={(e)=>{setData({...data,cpassword:e.target.value})}}
                />  

            <input
                type="number"
                placeholder='Registration Number' 
                value={data.regNo}
                onChange={(e)=>{setData({...data,regNo:e.target.value})}}
            />  
            <label>Select Establishment Year:</label>
            <select value="" 
            onChange={(e)=>{setData({...data,estb:e.target.value})}}>
                <option value={data.estb}>Select</option>
                {renderYearOptions()}
            </select>
             <input
                type="file"
                onChange={(e)=>{setProfile(e.target.files[0])}}
                />  
            <input
            onClick={()=>{handleSubmit()}}
                type="submit"
                value='Register' 
                />       
            <Link to="/login/ngo" style={{alignSelf:"flex-start",padding:"0px 7.4vw"}} >Already have a NGO Account?</Link> <br/>
        </div>

    </div>
    </div>
  )
}

export default RegisterNgo
