import React,{useEffect} from 'react'
import { useParams } from 'react-router-dom'
import './EventId.css'
import { Link } from 'react-router-dom'
import { useState } from 'react'
import { useSelector } from 'react-redux'
import Navbar from '../NavBar/Navbar'
const EventId = () => {
    const {id} = useParams();
    const notify=(message,type)=>{
      if(type==="success")
      {
          toast.success(message, {
              position: "top-right",
              autoClose: 5000,
              hideProgressBar: false,
              closeOnClick: true,
              pauseOnHover: true,
              draggable: true,
              progress: undefined,
              theme: "colored",
              });
      }
      else{
          toast.error(message, {
              position: "top-right",
              autoClose: 5000,
              hideProgressBar: false,
              closeOnClick: true,
              pauseOnHover: true,
              draggable: true,
              progress: undefined,
              theme: "colored",
              });
      }

  }
      const [event, setEvent] = useState(null);
      const [ngo, setNgo] = useState(null);
    const user = useSelector((state)=>{return state.user});
    const fetchEvent = async()=>{
      const response = await fetch(`http://localhost:8001/event/${id}`);
      const json = await response.json();
      setEvent(json.data);
  }
  const participate=async(eventId,userId)=>{
    console.log(eventId+" "+userId)
    const response = await fetch(`http://localhost:8001/event/participate`,{
      method:"POST",
      headers:{
        "content-type":"application/json"
      },
      body: JSON.stringify({userId,eventId})
    });
    const json = await response.json();


  }
  const fetchNgo = async()=>{
    if(event!==null)
    {
      const response = await fetch(`http://localhost:8001/ngo/${event.organiser}`);
      const json = await response.json();
      setNgo(json.data);

    }
}
  useEffect(() => {
    fetchEvent();
    fetchNgo();
  }, [event,ngo])

  return (
    <div>
      <Navbar/>
      {
        event &&   <div>  
          <img src= {`http://localhost:8001/assets/${event.image}`} 
          alt="" 
          id="eventImg" 
          className="eventImg" />
        <div className="eventInfo">
          <h1>{event.title}</h1>
          <p>{event.description}</p>
          <p>Start date: <span id="startDate">{event.startDate}</span></p>
          <p>End date: <span id="endDate">{event.endDate}</span></p>
          {/* <p>No. of participants: <span id="noOfParticipants">100</span></p> */}
          {user && !user.estb ?<button onClick={()=>{participate(event._id,user._id)}}>Participate</button> :"" } 
        </div>
        </div>
      }
      { ngo && <>   <div className="organiser">
        <h2>About Organiser</h2>
        <div className="aboutOrganiser">
          <div className="organImg">
            <img src={`http://localhost:8001/assets/${ngo.profile}`} alt="" id="organImg" />
            <Link to={`/ngo/${ngo._id}`}>View profile</Link>
          </div>
          <div className="organAbt">
            <h3>NGO Name: {ngo.name}</h3>
            <span>Year of Etablishment{ngo.estb}</span>
            {/* <span>0 Followers</span> */}
          </div>
        </div>
      </div>
      </> }

    </div>

  )
}


export default EventId;
