import React,{useEffect,useState} from 'react'
import { FaMapMarkerAlt } from 'react-icons/fa';
import './Events.css'
import { Link } from 'react-router-dom';
const Events = () => {
    const [events, setEvents] = useState([]);
    const fetchEvents = async ()=>{
        const response = await fetch("http://localhost:8001/events/");
        const json = await response.json();
        setEvents(json.data);  
    }
    useEffect(() => {
        fetchEvents();
    }, [])

  return (
    <div className='events'>
        <h1>Events</h1>
        <div className="filter">

        </div>
        <ul>
            { events.map((e,i)=>{
                return <li key={i}> <div className='eventBox'>
                        <div className="top" style={{backgroundImage: `url(${e.image})`}}></div>
                        <div className="bottom">
                            <h3>{e.title}</h3>
                            <p>{e.description}</p>
                            <p>{e.participants.length} participanting</p>
                            <p><FaMapMarkerAlt/>Location: {e.location}</p>
                            <Link to={`/event/${e._id}`}>Check Out</Link>
                        </div>
                    </div> </li>
            }) }
        </ul>
    </div>
  )
}

export default Events
