import React,{useState} from 'react'
import {useNavigate} from 'react-router-dom';
import './CreateEvent.css';
const CreateEvent = () => {
    const [data,setData] = useState({
        name:"",
        email:"",
        password:"",
        cpassword:"",
        regNo:"",
        estb:"",
    });
    const [profile,setProfile] = useState("");
    const navigate = useNavigate();
    const handleSubmit = async(e)=>{
        try {
            const formData = new FormData();
            formData.append("name",data.name);
            formData.append("email",data.email);
            formData.append("password",data.password);
            formData.append("profile",profile);
            formData.append("regNo",data.regNo);
            formData.append("estb",data.estb);

            const response = await fetch("http://localhost:8001/ngo/register",{
                method:"POST",
                body:formData
            });
            const json = await response.json();
            console.log(json)
            if(json.success) 
            {
                alert("Created");
                navigate("/login/ngo");
            }
            else 
                alert("failed");
        }catch(err)
        {
            alert(err.message);
        }   
    }
  return (
    <div className='create-event-body'>
        <h1>REGISTER NGO</h1>
         <div className="login-container">
        <div className="form">

            <input
                type="text"
                placeholder='NGO Name' 
                value={data.name}
                onChange={(e)=>{setData({...data,name:e.target.value})}}
                />

            <input
                type="email"
                placeholder='Email' 
                value={data.email}
                onChange={(e)=>{setData({...data,email:e.target.value})}}
                />
            <input
                type="password"
                placeholder='Password' 
                value={data.password}
                onChange={(e)=>{setData({...data,password:e.target.value})}}
                />
            <input
                type="password"
                placeholder='Confirm Password' 
                value={data.cpassword}
                onChange={(e)=>{setData({...data,cpassword:e.target.value})}}
                />  

            <input
                type="number"
                placeholder='Registration Number' 
                value={data.regNo}
                onChange={(e)=>{setData({...data,regNo:e.target.value})}}
            />  
             <input
                type="Date"
                placeholder='Year of Establishment' 
                value={data.estb}
                onChange={(e)=>{setData({...data,estb:e.target.value})}}
            />  
             <input
                type="file"
                onChange={(e)=>{setProfile(e.target.files[0])}}
                />  
            <input
            onClick={()=>{handleSubmit()}}
                type="submit"
                value='Register' 
                />       
        </div>

    </div>
    </div>
  )
}

export default CreateEvent
