import React,{useEffect, useState} from 'react'
const Admin = () => {
    const [ngos,setngos] = useState(null);
    const fectchAllNgos =async()=>{
        const response = await fetch("http://localhost:8001/ngo/");
        const json = await response.json();
        setngos(json.data);

    }
    useEffect(() => {
      fectchAllNgos();
    }, []);
    const verify = async(id)=>{
        console.log("first")
        const response = await fetch(`/ngo//${id}/verify`, {
            method: 'PUT',
            headers: {
              'Content-Type': 'application/json'
            }
          });
          const json = await response.json();
          if(json.success)
          {
            alert("Updated");
          }else{
            console.log("failed");
          }
    }

  return (
    <div className='admin'>
        <h1>Unverified NGOs</h1>
        {ngos && ngos.map((e,id)=>{return !e.verified?  <div id={id} className="ngo-box">
            <div className="left">
            <img src={`http://localhost:8001/assets/${e.profile}`} alt="" />
            </div>
            <div className="right">
            <h3>Name:{e.name} </h3>
            <h3>Establishment Year:{e.estb} </h3>
            <button onClick={()=>{verify(e._id)}} >Verify</button>
            </div>

        </div>:""})}
    </div>
  )
}

export default Admin
